import React, { useState, useRef } from "react";

const expensiveOperation = (ms) => {
  const start = performance.now();
  while (performance.now() - start < ms) {
    // do nothing
  }
};

const VirtualizationComparison = () => {
  const [activeTab, setActiveTab] = useState("virtualized");
  const itemCount = 1000;

  const virtualizedRef = useRef(null);
  const nonVirtualizedRef = useRef(null);

  const items = Array.from({ length: itemCount }, (_, i) => ({
    id: i,
    title: `Item ${i}`,
    description: `This is a description for item ${i}`,
    imageUrl: `data:image/jpeg;base64,`,
  }));

  // Virtualized list component
  const VirtualizedList = () => {
    const [startIndex, setStartIndex] = useState(0);
    const containerHeight = 400;
    const itemHeight =
      (document.querySelector(".list-item")?.clientHeight ?? 68) + 2;
    const visibleItems = Math.ceil(containerHeight / itemHeight) + 2; // +2 for buffer

    const handleScroll = (e) => {
      const scrollTop = e.target.scrollTop;
      const height = document.querySelector(".list-item").clientHeight;
      const index = Math.floor(scrollTop / (height + 2));
      setStartIndex(index);
    };

    return (
      <div
        ref={virtualizedRef}
        className="h-96 overflow-auto border border-gray-700 rounded"
        onScroll={handleScroll}
      >
        {/* Total height placeholder */}
        <div
          style={{
            height: `${items.length * itemHeight}px`,
            position: "relative",
          }}
        >
          {/* Only render visible items */}
          {items
            .slice(startIndex, startIndex + visibleItems)
            .map((item, index) => {
              expensiveOperation(10);
              return (
                <div
                  key={item.id}
                  className="list-item p-4 border border-gray-800 flex items-center"
                  style={{
                    position: "absolute",
                    top: `${(startIndex + index) * itemHeight}px`,
                    height: `${itemHeight}px`,
                    left: 0,
                    right: 0,
                    display: "flex",
                  }}
                >
                  <img
                    src={item.imageUrl}
                    alt={item.title}
                    className="mr-4 h-12 w-12"
                  />
                  <div>
                    <h3 className="font-bold">{item.title}</h3>
                    <p className="text-sm text-gray-600">{item.description}</p>
                  </div>
                </div>
              );
            })}
        </div>
      </div>
    );
  };

  // Non-virtualized list component
  const NonVirtualizedList = () => {
    return (
      <div
        ref={nonVirtualizedRef}
        className="h-96 overflow-auto border border-gray-700 rounded"
      >
        {items.map((item) => (
          <div
            key={item.id}
            className="list-item p-4 border-gray-800 flex items-center h-20"
            style={{ display: "flex" }}
          >
            <img
              src={item.imageUrl}
              alt={item.title}
              className="mr-4 h-12 w-12"
            />
            <div>
              <h3 className="font-bold">{item.title}</h3>
              <p className="text-sm text-gray-600">{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-6">
      <div className="flex flex-col space-y-4">
        <div className="flex space-x-4">
          <button
            className={`px-4 py-2 rounded ${
              activeTab === "virtualized"
                ? "bg-blue-800 text-white"
                : "bg-gray-900"
            }`}
            onClick={() => setActiveTab("virtualized")}
          >
            Virtualized
          </button>
          <button
            className={`px-4 py-2 rounded ${
              activeTab === "non-virtualized"
                ? "bg-blue-800 text-white"
                : "bg-gray-900"
            }`}
            onClick={() => setActiveTab("non-virtualized")}
          >
            Non-Virtualized
          </button>
        </div>
      </div>

      <div className="p-4">
        {activeTab === "virtualized" ? (
          <VirtualizedList />
        ) : (
          <NonVirtualizedList />
        )}
      </div>
    </div>
  );
};

export default VirtualizationComparison;
